import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { useEffect } from 'react';
import { Breadcrumbs, Button, TableFooter } from '@material-ui/core';
import { Link } from 'react-router-dom';
import useBreadcrumbs, { routeConfig } from "../../../hooks/useBreadcrumbs"
import { MdKeyboardArrowRight } from 'react-icons/md';
import { useDispatch, useSelector } from 'react-redux';
import { fetchCourses, deleteCourse } from '../../../store/actions/courses.action';
import ErrorTable from './errorTable';
import EmptyTable from './emptyTable';
import LoadingTable from './loadingTable'
import CourseRow from './courseRow';
import { useState } from 'react';
import FilterBar from './filter';
import { IoMdRefresh } from 'react-icons/io';

const CourseList = () => {
    const initialFilter = {
        mamh: '',
        tenmh: '',
        sotinchi: null,
        bomon: '',
        phanloai: ''
    }

    const dispatch = useDispatch()
    const breadcrumbs = useBreadcrumbs(routeConfig)
    const courseList = useSelector(state => state.courses)
    const [filter, setFilter] = useState(initialFilter)
    const filteredCourseList = courseList.data ? courseList.data : []

    useEffect(() => {
        if (!courseList.data)
            dispatch(fetchCourses())
    }, [dispatch, courseList.data])

    const refresh = () => {
        if (!courseList.loading)
            dispatch(fetchCourses())
    }

    const handleDelete = mamh => {
        dispatch(deleteCourse(mamh))
    }

    return (
        <>
            <div>
                <Breadcrumbs separator={<MdKeyboardArrowRight size="1.2em" />} className="page-breadcrumb">
                    {breadcrumbs.map(breadcrumb => (
                        <Link to={breadcrumb.path}>
                            {breadcrumb.name}
                        </Link>
                    ))}
                </Breadcrumbs>
                <h3 className="page-title">
                    Danh sách các môn học
                </h3>
                <hr />
            </div>
            <TableContainer className="p-2" component={Paper}>
                <FilterBar
                    filter={filter}
                    setFilter={setFilter}
                    refresh={refresh}
                />
                <Table className="border-top" style={{ minWidth: "920px" }}>
                    <TableHead>
                        <TableRow>
                            <TableCell width="150px">Mã môn học</TableCell>
                            <TableCell>Tên môn học</TableCell>
                            <TableCell width="120px" align="center">Số tín chỉ</TableCell>
                            <TableCell width="190px">Bộ môn</TableCell>
                            <TableCell width="120px">Phân loại</TableCell>
                            <TableCell width="150px" align='center'>Xem chi tiết / Xóa</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {courseList.loading
                            ? <LoadingTable />
                            : filteredCourseList.length !== 0
                                ? filteredCourseList.map(row => (
                                    <CourseRow row={row} delete={handleDelete} />
                                ))
                                : <EmptyTable />
                        }
                        {courseList.error && <ErrorTable refresh={refresh} />}
                    </TableBody>
                    <TableFooter>
                        asd
                    </TableFooter>
                </Table>
            </TableContainer>
        </>
    )
}

export default CourseList
