import { FETCH_COURSES_PENDING, FETCH_COURSES_FAIL, FETCH_COURSES_SUCCESS, DELETE_COURSE } from '../constants/ActionTypes'


const state = {
    INIt123: {

    },
    asdasd: {

    }
}

const courseDetailReducer = (state = {}, action) => {
    switch (action.type) {
        case FETCH_COURSES_PENDING:
            const newState = { ...state }
            newState[action.payload.mamh] = {
                loading: true
            }
            return newState
        case FETCH_COURSES_SUCCESS:
            return {
                ...state,
                loading: false,
                data: action.payload
            }
        case FETCH_COURSES_FAIL:
            console.log(action.payload)
            return {
                ...state,
                loading: false,
                error: action.payload
            }
        case DELETE_COURSE:
            console.log(action.payload)
            return {
                ...state,
                loading: false,
                data: state.data.filter(item => item.mamh !== action.payload)
            }
        default:
            return state
    }
}

export default courseDetailReducer
