import { Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from "@material-ui/core"
import { useState } from "react"
import { useSelector } from "react-redux"
import { LoadingRows } from "../../SituationRow"
import CreateNew from "../CreateNew"

const CourseOutcome = ({ mamh }) => {
    const initialState = {
        loading: false,
        response: {
            status: "",
            message: ""
        }
    }

    const [create, setCreate] = useState(false)
    const [ID, setID] = useState('')
    const [desc, setDesc] = useState('')
    const [outcome, setOutcome] = useState('')
    const [creating, setCreating] = useState(initialState)

    const goals = useSelector(state => state.course[mamh].goal)
    const data = useSelector(state => state.course[mamh].goal.data)
    const dispatch = useDispatch()

    const fetch = () => {
        dispatch(fetchGoal(mamh))
    }

    const handleToggleCreate = () => {
        setID('')
        setDesc('')
        setOutcome('')
        setCreate(true)
    }

    const handleSubmitCreate = () => {
        setCreating({ ...creating, loading: true })
        createCourseGoal(mamh, {
            muctieu: ID,
            mota: desc,
            cdr_ctdt: outcome
        }).then(() => {
            fetch()
            setCreate(false)
            setCreating({
                loading: false,
                response: {
                    status: "success",
                    message: "Tạo mục tiêu thành công!"
                }
            })
        })
            .catch(err => {
                setCreating({
                    loading: false,
                    response: {
                        status: "error",
                        message: !err.response ? "Lỗi máy chủ" :
                            err.response.data.error
                    }
                })
            })
    }

    useEffect(() => {
        if (!goals.data)
            dispatch(fetchGoal(mamh))
    }, [dispatch, mamh, goals.data])

    return <>
        <TableContainer className="p-2" component={Paper}>
            <CreateNew
                label="Tạo chuẩn đầu ra"
            />
            <Table style={{ minWidth: "700px" }}>
                <TableHead>
                    <TableRow>
                        <TableCell width="100px" align="center" size="small">
                            Mục tiêu
                    </TableCell>
                        <TableCell width="110px" align="center" size="small">
                            Chuẩn đầu ra HP
                    </TableCell>
                        <TableCell align="center" size="small">
                            Mô tả
                    </TableCell>
                        <TableCell width="150px" align="center" size="small">
                            Chuẩn đầu ra CDIO
                    </TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    <LoadingRows col={4} />
                </TableBody>
            </Table>
        </TableContainer>
    </>
}

export default CourseOutcome
