import { Backdrop, Breadcrumbs, Button, Card, CardActions, CardContent, CardHeader, CircularProgress, IconButton, Snackbar } from "@material-ui/core"
import { useState } from "react"
import axios from 'axios'
import useBreadcrumbs, { routeConfig } from "../../../hooks/useBreadcrumbs"
import { MdKeyboardArrowRight } from 'react-icons/md'
import { Link } from "react-router-dom"
import styled from "styled-components"

const InputLabel = styled.label`
    font-size: 13px;
`

const Input = styled.input`
 &:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
`

const TextArea = styled.textarea`
 &:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
 resize:none;
`

const Select = styled.select`
&:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
`

const FixedWidthForm = styled.form`
 min-width:450px;
`

const NewCourse = () => {
    const [mamonhoc, setMamonhoc] = useState('')
    const [tenmonhoc, setTenmonhoc] = useState('')
    const [tinchi, setTinchi] = useState(1)
    const [phanloai, setPhanloai] = useState('')
    const [mota, setMota] = useState('')
    const [bomon, setBomon] = useState('')
    const [alert, setAlert] = useState(false)
    const breadcrumbs = useBreadcrumbs(routeConfig)

    const postCourse = data => {
        axios.post('http://localhost:3001/api/monhoc', {
            mamh: mamonhoc,
            tenmh: tenmonhoc,
            sotinchi: tinchi,
            phanloai,
            mota,
            mabomon: bomon
        }).catch(err => alert(err))
    }

    const handleSubmit = async (e) => {
        e.preventDefault()
        postCourse({

        })
    }

    return <>
        <Backdrop open={true} style={{zIndex:2000}}>
            <div className="spinner-border text-white" />
        </Backdrop>
        <div>
            <Breadcrumbs separator={<MdKeyboardArrowRight size="1.2em" />} className="page-breadcrumb">
                {breadcrumbs.map(breadcrumb => (
                    <Link to={breadcrumb.path}>
                        {breadcrumb.name}
                    </Link>
                ))}
            </Breadcrumbs>
            <h3 className="page-title">
                Thêm môn học mới
            </h3>
            <hr />
        </div>
        <Card className="feature-card">
            <CardHeader
                title="Nhập thông tin môn học"
                className="border-bottom "
                disableTypography
            />
            <CardContent className="border-bottom overflow-auto">
                <FixedWidthForm id="create-form">
                    <div className="form-group col-12">
                        <InputLabel for="tenmh">Tên môn học</InputLabel>
                        <Input
                            required
                            id="tenmh"
                            name="tenmh"
                            type="text"
                            className="bg-light form-control"
                            value={tenmonhoc}
                            onChange={e => setTenmonhoc(e.target.value)}
                        />
                    </div>
                    <div className="row mx-0">
                        <div className="form-group col-3 col-md-2">
                            <InputLabel for="mamh">Mã môn học</InputLabel>
                            <Input
                                required
                                name="mamh"
                                id="mamh"
                                type="text"
                                className="bg-light form-control"
                                value={mamonhoc}
                                onChange={e => setMamonhoc(e.target.value)}
                            />
                        </div>
                        <div className="form-group col-3 col-md-2">
                            <InputLabel for="sotc">Số tín chỉ</InputLabel>
                            <Input
                                required
                                name="sotc"
                                id="sotc"
                                type="number"
                                className="bg-light form-control"
                                min={1}
                                max={10}
                                value={tinchi}
                                onKeyDown={e => {
                                    if ((e.target.value === '' && e.key === '0') ||
                                        e.key === 'e' || e.key === '.' || e.key === '-') {
                                        e.preventDefault()
                                        e.stopPropagation();
                                    }


                                }}
                                onChange={e => {
                                    if (e.target.value > 10)
                                        setTinchi(10)
                                    else
                                        setTinchi(e.target.value)

                                }}
                            />
                        </div>

                        <div className="form-group col-12 col-lg-4">
                            <InputLabel>Bộ môn</InputLabel>
                            <Select
                                className="form-control bg-light"

                            >
                                <option selected value="" disabled>-- Chọn bộ môn --</option>
                                <option value="thcs">Tin học cơ sở</option>
                                <option value="cnpm">Công nghệ phần mềm</option>
                                <option value="httt">Hệ thống thông tin</option>
                                <option value="manm">Mạng và an ninh mạng</option>
                            </Select>
                        </div>
                        <div className="form-group col-12 col-lg-4">
                            <InputLabel>Phân loại</InputLabel>
                            <Select className="form-control bg-light">
                                <option selected value="" disabled>-- Chọn phân loại --</option>
                                <option value="tc">Tùy chọn</option>
                                <option value="bb">Bắt buộc</option>
                            </Select>
                        </div>
                    </div>
                    <div className="form-group col-12">
                        <InputLabel for="mota">Mô tả</InputLabel>
                        <TextArea
                            required
                            name="mota"
                            id="mota"
                            type=""
                            className="bg-light form-control scrollbar-primary"
                            rows={10}
                            value={mota}
                            onChange={e => setMota(e.target.value)}
                        />
                    </div>
                </FixedWidthForm>
            </CardContent>
            <CardActions className="justify-content-end">
                <Button
                    onSubmit={handleSubmit}
                    variant="contained"
                    color="primary"
                    type="submit"
                    form="create-form"
                >
                    Tạo môn học mới
                    </Button>
            </CardActions>
        </Card>
        <Snackbar
            anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left'
            }}
            open={alert}
            autoHideDuration={1500}
            message="Success"
            action={
                <>
                    <IconButton
                        size="small"
                        aria-label="close"
                        color="inherit"
                        onClick={() => setAlert(false)}
                    >
                        X
                    </IconButton>
                </>
            }
        />
    </>
}

export default NewCourse
