import { Button, TextField } from "@material-ui/core"
import { useState } from "react"
import axios from 'axios'


const NewCourse = () => {
    const [mamonhoc, setMamonhoc] = useState('')
    const [tenmonhoc, setTenmonhoc] = useState('')
    const [tinchi, setTinchi] = useState(null)
    const [phanloai, setPhanloai] = useState('')
    const [mota, setMota] = useState('')
    const [bomon, setBomon] = useState('')
    const [post, setPost] = useState(false)

    const handleSubmit = e => {
        setPost(true)
        axios.post('http://localhost:3001/api/monhoc', {
            mamh: mamonhoc,
            tenmh: tenmonhoc,
            sotinchi: tinchi,
            phanloai,
            mota,
            ma_bomon: bomon
        }).then(res => {
            alert(res)
            setPost(false)
        }).catch(err =>{
            alert(err)
            setPost(false)
        })
    }

    return <>
        {post && <div class="spinner-border"></div>}
        <div>
            <h3 className="page-title">
                Them mon hoc
            </h3>
            <hr />
        </div>
        <div>
            <form>
                <TextField value={mamonhoc} onChange={e => setMamonhoc(e.target.value)} label="Mã môn học" />
                <TextField value={tenmonhoc} onChange={e => setTenmonhoc(e.target.value)} label="Tên môn học" />
                <TextField value={tinchi} onChange={e => setTinchi(e.target.value)} type="number" label="Số tín chỉ" />
                <TextField value={phanloai} onChange={e => setPhanloai(e.target.value)} label="Phân loại" />
                <TextField value={mota} onChange={e => setMota(e.target.value)} label="Mô tả" multiline />
                <TextField value={bomon} onChange={e => setBomon(e.target.value)} label="Bộ môn" />
                <Button onClick={handleSubmit} variant="contained" color="primary" fullWidth>Tạo</Button>
            </form>
        </div>
    </>
}

export default NewCourse
