import { CircularProgress, IconButton, TableCell, TableRow } from "@material-ui/core"
import { useState } from "react"
import { AiOutlineEdit } from "react-icons/ai"
import { BsTrash } from "react-icons/bs"
import { useDispatch } from "react-redux"
import { deleteCourseGoal } from "../../../services"
import { deleteGoalSuccess } from "../../../store/actions/courseGoal.action"

const Row = ({ row, mamh }) => {
    const [loading, setLoading] = useState(false)
    const [flag, setFlag] = useState({
        type: '',
        message: ''
    })
    const dispatch = useDispatch()

    const handleDelete = async () => {
        setLoading(true)
        deleteCourseGoal(mamh, row.muctieu)
            .then(() => {
                console.log("DELETE_ROW")
                dispatch(deleteGoalSuccess(mamh, row.muctieu))
            })
            .catch(err => {
                setLoading(false)
                console.log(err)
            })
    }

    return (
        <>
            <TableRow style={{ transform: "scale(1)" }}>
                <TableCell align="center">
                    {row.muctieu}
                </TableCell>
                <TableCell>
                    {row.mota}
                </TableCell>
                <TableCell align="center">
                    {row.cdr_ctdt}
                </TableCell>
                <TableCell align='center'>
                    <IconButton className="p-0 mx-3 text-secondary">
                        <AiOutlineEdit size="24px" />
                    </IconButton>
                    <IconButton className="p-0 mx-3 text-danger">
                        <BsTrash
                            size="24px"
                            onClick={handleDelete}
                        />
                    </IconButton>
                </TableCell>
                {loading &&
                    <TableCell style={{ top: 0, left: 0 }}
                        className="position-absolute overlay flex-center p-0 w-100 h-100">
                        <CircularProgress className="text-white" />
                    </TableCell>
                }
            </TableRow>
        </>
    )
}

export default Row
