import { useState } from 'react'
import { MdHome, MdKeyboardArrowDown } from 'react-icons/md'
import { VscClose } from 'react-icons/vsc'
import { BiDownArrow } from 'react-icons/bi'
import { NavLink } from 'react-router-dom'
import styled from 'styled-components'

const CloseButton = styled.button`
    padding:0;
    background-color:transparent;
    border:none;
    color:inherit;
`

const Arrow = styled(MdKeyboardArrowDown)`
    transition: all 150ms ease-in-out;
    &.collapsed{
        transform: rotate(180deg);
    }
`

const Collapse = (props) => {
    const [collapsed, setCollapsed] = useState(true)

    return <>
        <div onClick={() => setCollapsed(!collapsed)} className="sidebar-list">
            {props.text}
            <Arrow size="25px" className={`${collapsed ? "collapsed" : ""}`} />
        </div>
        {collapsed && (
            <div>
                {props.children}
            </div>
        )}
    </>
}

const Sidebar = (props) => {
    return (
        <div className="sidebar">
            <header className="sidebar-header">
                <CloseButton onClick={props.close}>
                    <VscClose size="25px" />
                </CloseButton>
            </header>
            <div className="sidebar-content">
                <NavLink exact to='/' className="sidebar-item">
                    s
                </NavLink>
                <Collapse text="MonHoc">
                    <NavLink exact to='/' className="sidebar-item nested">
                        s
                    </NavLink>
                </Collapse>
            </div>
            <footer className="sidebar-footer">
                <small>Copyright Â© 2011 HCMUTE - All Rights Reserved</small>
            </footer>
        </div>
    )
}

export default Sidebar
