import {
    FETCH_COURSE_PENDING,
    FETCH_COURSE_FAIL,
    FETCH_COURSE_SUCCESS
}
    from '../constants/ActionTypes'



const courseDetailReducer = (state = {}, action) => {
    console.log(state)
    let course
    switch (action.type) {
        case FETCH_COURSE_PENDING:
            course[action.payload.mamh] = {
                loading: true,
                error: ''
            }
            return {
                ...state,
                ...course
            }
        default:
            return state
    }
}

export default courseDetailReducer
