import { useState } from "react";
import { getCourseGoalList } from "../../../services";
import { LoadingCellOverlay, LoadingOverlay } from "../../StatelessComponents";

const { TableRow, TableCell, Input } = require("@material-ui/core");

const CreateForm = ({ mamh, id, setId, goal, setGoal, desc, setDesc, cdio, setCDIO, loading }) => {
    const initialFetchState = {
        loading: false,
        error: null,
        data: null
    }
    const [fetchGoal, setFetchGoal] = useState(initialFetchState)

    useState(() => {
        setFetchGoal({ ...initialFetchState, loading: true })
        getCourseGoalList(mamh)
            .then(data => {
                setFetchGoal({
                    ...initialFetchState,
                    loading: false,
                    data
                })
            })
            .catch(err => {
                setFetchGoal({
                    ...initialFetchState,
                    error: err.response
                })
            })
    }, [])

    if (fetchGoal.loading) {
        return <div>
            <LoadingOverlay />
        </div>
    }

    return <>
        <TableRow className="font-italic">
            <TableCell align="center" className="font-weight-bold">
                <Input
                    className="bg-light rounded" fullWidth
                    inputProps={{ style: { textTransform: "uppercase" } }}
                    value={goal}
                    onChange={e => setGoal(e.target.value)}
                />
            </TableCell>
            <TableCell align="center" className="font-weight-bold">
                <Input
                    className="bg-light rounded" fullWidth
                    inputProps={{ style: { textTransform: "uppercase" } }}
                    value={id}
                    onChange={e => setId(e.target.value)}
                />
            </TableCell>
            <TableCell>
                <Input
                    className="bg-light rounded p-2"
                    rowsMax={6} fullWidth multiline
                    value={desc}
                    onChange={e => setDesc(e.target.value)}
                />
            </TableCell>
            <TableCell>
                <Input
                    className="bg-light rounded" fullWidth
                    value={cdio}
                    onChange={e => setCDIO(e.target.value)}
                />
            </TableCell>
            {
                loading && <LoadingCellOverlay />
            }
        </TableRow>
    </>
}

export default CreateForm
