import { Paper, Snackbar, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from "@material-ui/core"
import { Alert, AlertTitle } from "@material-ui/lab"
import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { createCourseGoal } from "../../../services"
import { fetchGoal } from "../../../store/actions/courseGoal.action"
import { EmptyRow, ErrorRow, LoadingRows } from "../../SituationRow"
import CreateNew from "../CreateNew"
import CreateForm from './createForm'
import Row from './row'

const CourseGoal = ({ mamh }) => {
    const initialState = {
        loading: false,
        response: {
            status: "",
            message: ""
        }
    }

    const [create, setCreate] = useState(false)
    const [ID, setID] = useState('')
    const [desc, setDesc] = useState('')
    const [outcome, setOutcome] = useState('')
    const [creating, setCreating] = useState(initialState)

    const goals = useSelector(state => state.course[mamh].goal)
    const data = useSelector(state => state.course[mamh].goal.data)
    const dispatch = useDispatch()

    const fetch = () => {
        dispatch(fetchGoal(mamh))
    }

    const handleToggleCreate = () => {
        setID('')
        setDesc('')
        setOutcome('')
        setCreate(true)
    }

    const handleSubmitCreate = () => {
        setCreating({ ...creating, loading: true })
        createCourseGoal(mamh, {
            muctieu: ID,
            mota: desc,
            cdr_ctdt: outcome
        }).then(() => {
            fetch()
            setCreate(false)
            setCreating({
                loading: false,
                response: {
                    status: "success",
                    message: "Tạo mục tiêu thành công!"
                }
            })
        })
            .catch(err => {
                setCreating({
                    loading: false,
                    response: {
                        status: "error",
                        message: !err.response ? "Lỗi máy chủ" :
                            err.response.data.error
                    }
                })
            })
    }

    useEffect(() => {
        if (!goals.data)
            dispatch(fetchGoal(mamh))
    }, [dispatch, mamh, goals.data])

    return <>
        <Snackbar
            open={creating.response.status}
            onClose={() => setCreating(initialState)}
        >
            <Alert
                onClose={() => setCreating(initialState)}
                severity={creating.response.status}
                className="mx-3 mb-3"
                style={{ minWidth: "250px" }}
            >
                <AlertTitle className="text-capitalize font-weight-bold">
                    {creating.response.status}
                </AlertTitle>
                {creating.response.message}
            </Alert>
        </Snackbar>
        <CreateForm
            open={create}
            id={ID}
            desc={desc}
            outcome={outcome}
            setId={setID}
            setDesc={setDesc}
            setOutcome={setOutcome}
            loading={creating.loading}
        />
        <TableContainer className="p-2" component={Paper}>
            <CreateNew
                fetch={fetch}
                setOpen={setCreate}
                open={create}
                pending={goals.pending}
                handleSubmitCreate={handleSubmitCreate}
                handleToggleCreate={handleToggleCreate}
                label="Tạo mục tiêu"
            />
            <Table style={{ minWidth: "750px" }}>
                <TableHead>
                    <TableRow>
                        <TableCell width="120px" align="center" size="small">
                            Mục tiêu
                        </TableCell>
                        <TableCell align="center" size="small">
                            Mô tả
                        </TableCell>
                        <TableCell width="150px" align="center" size="small">
                            Chuẩn đầu ra CTĐT
                        </TableCell>
                        <TableCell width="150px" className="px-0" align="center" size="small">
                        </TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {
                        goals.pending
                            ? <LoadingRows col={4} />
                            : goals.error
                                ? <ErrorRow refresh={fetch} />
                                : goals.data && data.length !== 0
                                    ? data.map(row => (
                                        <Row row={row} mamh={mamh} key={row.muctieu} />
                                    ))
                                    : <EmptyRow
                                        error="Chưa có mục tiêu"
                                        subError={(<>
                                            Vui lòng&nbsp;
                                        <span onClick={() => setCreate(true)} style={{ cursor: "pointer" }} class="btn-link">
                                                tạo mới!
                                        </span>
                                        </>)}
                                    />
                    }
                </TableBody>
            </Table>
        </TableContainer>
    </>
}

export default CourseGoal
