import styled from "styled-components"

const InputLabel = styled.label`
    font-size: 13px;
`

const Input = styled.input`
 &:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
`

const TextArea = styled.textarea`
 &:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
 resize:none;
`

const Select = styled.select`
&:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
`

const FixedWidthForm = styled.form`
 min-width:450px;
`

const ErrorText = styled.span`
    position:absolute;
    font-size:11px;
    transform:translate(3px,2px);
    color:var(--danger);
    font-style:italic;
`

export const NewCourseForm = (props) => (
    <div>
        <FixedWidthForm id="create-form">
            <div className="form-group col-12 pb-2">
                <InputLabel
                    className={`${errors.tenmh && 'text-danger'}`}
                    for="tenmh">Tên môn học</InputLabel>
                <Input
                    id="tenmh"
                    name="tenmh"
                    type="text"
                    className={`bg-light form-control
                            ${errors.tenmh && ' border-danger'}`}
                    value={tenmh}
                    onChange={e => setTenmh(e.target.value)}
                />
                <ErrorText>{errors.tenmh}</ErrorText>
            </div>
            <div className="row mx-0">
                <div className="form-group col-3 col-md-3 col-lg-2 pb-2">
                    <InputLabel
                        className={`${errors.mamh && 'text-danger'}`}
                        for="mamh"
                    >Mã môn học</InputLabel>
                    <Input
                        name="mamh"
                        id="mamh"
                        type="text"
                        className={`bg-light form-control
                                ${errors.mamh && ' border-danger'}`}
                        value={mamh}
                        onChange={e => setMamh(e.target.value)}
                    />
                    <ErrorText>{errors.mamh}</ErrorText>
                </div>
                <div className="form-group col-3 col-md-2 pb-2">
                    <InputLabel
                        for="sotc"
                        className={`${errors.sotinchi && 'text-danger'}`}
                    >
                        Số tín chỉ
                            </InputLabel>
                    <Input
                        name="sotc"
                        id="sotc"
                        type="number"
                        className={`bg-light form-control
                                ${errors.sotinchi && ' border-danger'}`}
                        min={1}
                        max={10}
                        value={sotinchi}
                        onKeyDown={e => {
                            if ((e.target.value === '' && e.key === '0') ||
                                e.key === 'e' || e.key === '.' || e.key === '-') {
                                e.preventDefault()
                                e.stopPropagation();
                            }
                        }}
                        onChange={e => {
                            if (e.target.value > 10)
                                setsotinchi(10)
                            else
                                setsotinchi(e.target.value)

                        }}
                    />
                    <ErrorText>{errors.sotinchi}</ErrorText>
                </div>
                <div className="form-group col-12 col-lg-4 pb-2">
                    <InputLabel
                        className={`${errors.bomon && 'text-danger'}`}
                    >Bộ môn</InputLabel>
                    <Select
                        className={`bg-light form-control
                            ${errors.bomon ? ' border-danger' : ''}`}
                        value={bomon}
                        onChange={e => setBomon(e.target.value)}
                    >
                        <option selected value="" disabled>-- Chọn bộ môn --</option>
                        <option value="thcs">Tin học cơ sở</option>
                        <option value="cnpm">Công nghệ phần mềm</option>
                        <option value="httt">Hệ thống thông tin</option>
                        <option value="manm">Mạng và an ninh mạng</option>
                    </Select>
                    <ErrorText>{errors.bomon}</ErrorText>
                </div>
                <div className="form-group col-12 col-lg-4 pb-2">
                    <InputLabel
                        className={`${errors.phanloai && 'text-danger'}`}
                    >
                        Phân loại
                            </InputLabel>
                    <Select
                        className={`bg-light form-control
                            ${errors.phanloai ? ' border-danger' : ''}`}
                        value={phanloai}
                        onChange={e => setPhanloai(e.target.value)}
                    >
                        <option selected value="" disabled>-- Chọn phân loại --</option>
                        <option>Tùy chọn</option>
                        <option>Bắt buộc</option>
                    </Select>
                    <ErrorText>{errors.phanloai}</ErrorText>
                </div>
            </div>
            <div className="form-group col-12 pb-2">
                <InputLabel for="mota">Mô tả (optional)</InputLabel>
                <TextArea
                    name="mota"
                    id="mota"
                    className="bg-light form-control scrollbar-primary"
                    rows={10}
                    value={mota}
                    onChange={e => setMota(e.target.value)}
                />
            </div>
        </FixedWidthForm>
    </div>
)
