const CreateForm = ({ mamh, id, setId, goal, setGoal, desc, setDesc, cdio, setCDIO, loading }) => {
    const initialFetchState = {
        loading: false,
        error: null,
        data: null
    }
    const [fetchGoal, setFetchGoal] = useState(initialFetchState)

    useState(() => {
        setFetchGoal({ ...initialFetchState, loading: true })
        getCourseGoalList(mamh)
            .then(data => {
                setFetchGoal({
                    ...initialFetchState,
                    loading: false,
                    data
                })
            })
            .catch(err => {
                setFetchGoal({
                    ...initialFetchState,
                    error: err.response
                })
            })
    }, [])

    if (fetchGoal.loading) {
        return <div>
            <LoadingOverlay />
        </div>
    }

    if (!fetchGoal.data)
        return

    return <>
        <TableRow className="font-italic">
            <TableCell align="center" className="font-weight-bold">
                <select value={goal} onChange={e => setGoal(e.target.value)}>
                    {fetchGoal.data.map(goal => (
                        <option>
                            {goal}
                        </option>
                    ))}
                </select>
            </TableCell>
            <TableCell align="center" className="font-weight-bold">
                <Input
                    className="bg-light rounded" fullWidth
                    inputProps={{ style: { textTransform: "uppercase" } }}
                    value={id}
                    onChange={e => setId(e.target.value)}
                />
            </TableCell>
            <TableCell>
                <Input
                    className="bg-light rounded p-2"
                    rowsMax={6} fullWidth multiline
                    value={desc}
                    onChange={e => setDesc(e.target.value)}
                />
            </TableCell>
            <TableCell>
                <Input
                    className="bg-light rounded" fullWidth
                    value={cdio}
                    onChange={e => setCDIO(e.target.value)}
                />
            </TableCell>
            {
                loading && <LoadingCellOverlay />
            }
        </TableRow>
    </>
}

import { Button, Drawer, Input } from "@material-ui/core";
import styled from "styled-components";

const Label = styled.label`
    font-size:13px;
    font-weight:600;
`

const GoalForm = (props) => {
    return <Drawer
        open={props.open}
        anchor="right"
    >
        <div className="detail-form__wrapper">
            <header className="detail-form__header">
                {props.header}
            </header>
            <div className="detail-form__main">
                <div className="mt-2">
                    <Label for="goal">
                        Mục tiêu
                    </Label>
                </div>
                <div className="mt-2">
                    <Label for="goal">
                        Mục tiêu
                    </Label>
                    <Input
                        id="goal"
                        value={id}
                        onChange={e => setId(e.target.value)}
                        fullWidth
                        disableUnderline
                        inputProps={{
                            style: {
                                fontFamily: "Open Sans"
                            },
                            className: "grey-200-bg border p-2 rounded"
                        }}
                    />
                </div>
                <div className="mt-2">
                    <Label for="outcome">
                        Chuẩn đầu ra CTĐT
                    </Label>
                    <Input
                        id="outcome"
                        value={outcome}
                        onChange={e => setOutcome(e.target.value)}
                        fullWidth
                        disableUnderline
                        inputProps={{ className: "grey-200-bg border p-2 rounded" }}
                    />
                </div>
                <div className="mt-2">
                    <Label for="desc">
                        Mô tả
                    </Label>
                    <Input
                        id="desc"
                        value={desc}
                        onChange={e => setDesc(e.target.value)}
                        rows={10}
                        rowsMax={255}
                        inputProps={{ className: "grey-200-bg border p-2 rounded" }}
                        fullWidth
                        disableUnderline
                        multiline
                    />
                </div>
            </div>
            <div className="detail-form__footer">
                <Button
                    className="text-transform-none light-blue-bgcolor"
                    color="primary"
                    variant="contained"
                    onClick={handleSubmit}
                    disabled={!id || !desc || !outcome}
                >
                    Tạo mới
                </Button>
                <Button
                    className="text-transform-none ml-3"
                    color="secondary"
                    variant="outlined"
                    onClick={setClose}
                >
                    Đóng
                </Button>
            </div>
        </div>
    </Drawer>
}

export default GoalForm
