import { useLocation } from 'react-router-dom'

const useBreadcrumbs = (routeConfig) => {
    const paths = []
    const route = '/'
    var position = 1;
    while (position !== -1) {
        paths.push(route.slice(0, position))
        position = route.indexOf('/', position + 1)
        if (position === -1)
            paths.push(route)
    }
    console.log(paths)
}

export default useBreadcrumbs
