import { Backdrop, Breadcrumbs, Button, Card, CardActions, CardContent, CardHeader, CircularProgress, IconButton, Snackbar } from "@material-ui/core"
import { useState } from "react"
import useBreadcrumbs, { routeConfig } from "../../../hooks/useBreadcrumbs"
import { MdKeyboardArrowRight } from 'react-icons/md'
import { Link } from "react-router-dom"
import styled from "styled-components"
import { createCourse } from "../../../services"

const InputLabel = styled.label`
    font-size: 13px;
`

const Input = styled.input`
 &:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
`

const TextArea = styled.textarea`
 &:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
 resize:none;
`

const Select = styled.select`
&:focus{
     outline:none;
     box-shadow:none;
 }
 border-radius:10px;
`

const FixedWidthForm = styled.form`
 min-width:450px;
`

const NewCourse = () => {
    const [mamh, setMamh] = useState('')
    const [tenmh, setTenmh] = useState('')
    const [sotinchi, setsotinchi] = useState(1)
    const [phanloai, setPhanloai] = useState('')
    const [mota, setMota] = useState('')
    const [bomon, setBomon] = useState('')
    const [posting, setPosting] = useState(false)
    const [errors, setErrors] = useState({
        mamh: '', tenmh: '', sotinchi: '', phanloai: '', bomon: ''
    })
    const breadcrumbs = useBreadcrumbs(routeConfig)

    const validateForm = () => {
        const error = {}
        if (mamh === '')
            error.mamh = 'Chưa nhập'
        if (tenmh === '')
            error.tenmh = 'Chưa nhập'
        if (phanloai === '')
            error.phanloai = 'Chưa chọn'
        if (bomon === '')
            error.bomon = 'Chưa chọn'
        if (sotinchi === '')
            error.sotinchi = 'Chưa chọn'
        setErrors({ ...error })
        if (Object.keys(error).length === 0)
            return true
        else
            return false
    }

    const handleSubmit = async (e) => {
        if (!validateForm())
            return
        setPosting(true)
        const data = {
            mamh, tenmh, sotinchi, phanloai, mota, bomon
        }
        console.log(data)
        try {
            await createCourse(data)
            alert("success!")
            window.location.reload()
            return
        }
        catch (err) {
            alert("Failed")
            console.log(err.response)
        }
        setPosting(false)

    }

    return <>
        <Backdrop open={posting} style={{ zIndex: 2000 }}>
            <CircularProgress className="text-white" />
        </Backdrop>
        <div>
            <Breadcrumbs separator={<MdKeyboardArrowRight size="1.2em" />} className="page-breadcrumb">
                {breadcrumbs.map(breadcrumb => (
                    <Link to={breadcrumb.path}>
                        {breadcrumb.name}
                    </Link>
                ))}
            </Breadcrumbs>
            <h3 className="page-title">
                Thêm môn học mới
            </h3>
            <hr />
        </div>
        <Card className="feature-card">
            <CardHeader
                title="Nhập thông tin môn học"
                className="border-bottom "
                disableTypography
            />
            <CardContent className="border-bottom overflow-auto">
                <FixedWidthForm id="create-form">
                    <div className="form-group col-12 pb-2">
                        <InputLabel for="tenmh">Tên môn học</InputLabel>
                        <Input
                            id="tenmh"
                            name="tenmh"
                            type="text"
                            className={`bg-light form-control
                            ${errors.tenmh ? ' border-danger' : ''}`}
                            value={tenmh}
                            onChange={e => setTenmh(e.target.value)}
                        />
                    </div>
                    <div className="row mx-0">
                        <div className="form-group col-3 col-md-2 pb-2">
                            <InputLabel for="mamh">Mã môn học</InputLabel>
                            <Input
                                name="mamh"
                                id="mamh"
                                type="text"
                                className="bg-light form-control"
                                value={mamh}
                                onChange={e => setMamh(e.target.value)}
                            />
                        </div>
                        <div className="form-group col-3 col-md-2 pb-2">
                            <InputLabel for="sotc">Số tín chỉ</InputLabel>
                            <Input
                                name="sotc"
                                id="sotc"
                                type="number"
                                className="bg-light form-control"
                                min={1}
                                max={10}
                                value={sotinchi}
                                onKeyDown={e => {
                                    if ((e.target.value === '' && e.key === '0') ||
                                        e.key === 'e' || e.key === '.' || e.key === '-') {
                                        e.preventDefault()
                                        e.stopPropagation();
                                    }


                                }}
                                onChange={e => {
                                    if (e.target.value > 10)
                                        setsotinchi(10)
                                    else
                                        setsotinchi(e.target.value)

                                }}
                            />
                        </div>

                        <div className="form-group col-12 col-lg-4 pb-2">
                            <InputLabel>Bộ môn</InputLabel>
                            <Select
                                className="form-control bg-light"
                                value={bomon}
                                onChange={e => setBomon(e.target.value)}
                            >
                                <option selected value="" disabled>-- Chọn bộ môn --</option>
                                <option value="thcs">Tin học cơ sở</option>
                                <option value="cnpm">Công nghệ phần mềm</option>
                                <option value="httt">Hệ thống thông tin</option>
                                <option value="manm">Mạng và an ninh mạng</option>
                            </Select>
                        </div>
                        <div className="form-group col-12 col-lg-4 pb-2">
                            <InputLabel>Phân loại</InputLabel>
                            <Select
                                className="form-control bg-light"
                                value={phanloai}
                                onChange={e => setPhanloai(e.target.value)}
                            >
                                <option selected value="" disabled>-- Chọn phân loại --</option>
                                <option>Tùy chọn</option>
                                <option>Bắt buộc</option>
                            </Select>
                        </div>
                    </div>
                    <div className="form-group col-12 pb-2">
                        <InputLabel for="mota">Mô tả</InputLabel>
                        <TextArea
                            name="mota"
                            id="mota"
                            type=""
                            className="bg-light form-control scrollbar-primary"
                            rows={10}
                            value={mota}
                            onChange={e => setMota(e.target.value)}
                        />
                    </div>
                </FixedWidthForm>
            </CardContent>
            <CardActions className="justify-content-end">
                <Button
                    onClick={handleSubmit}
                    variant="contained"
                    color="primary"
                    type="submit"
                    className="text-transform-none"
                >
                    Tạo môn học mới
                    </Button>
            </CardActions>
        </Card>
        {/* <Snackbar
            anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left'
            }}
            open={alert}
            autoHideDuration={1500}
            message="Success"
            action={
                <>
                    <IconButton
                        size="small"
                        aria-label="close"
                        color="inherit"
                    >
                        X
                    </IconButton>
                </>
            }
        /> */}
    </>
}

export default NewCourse
