import {
    ACTION_COURSE_GOAL_PENDING,
    FETCH_COURSE_GOAL_SUCCESS,
    ACTION_COURSE_GOAL_FAIL
}
    from '../constants/ActionTypes'
import { getCourseGoal } from '../../services'

const actionGoalPending = mamh => ({
    type: ACTION_COURSE_GOAL_PENDING,
    payload: mamh
})

const fetchGoalSuccess = data => ({
    type: FETCH_COURSE_GOAL_SUCCESS,
    payload: data
})

const actionGoalFail = (mamh, error) => ({
    type: ACTION_COURSE_GOAL_FAIL,
    payload: { mamh, error }
})

export const fetchGoal = mamh => {
    return dispatch => {
        dispatch(actionGoalPending(mamh))
        getCourseGoal(mamh)
            .then(data => ({ mamh, data }))
            .then(data => {
                dispatch(fetchGoalSuccess(data))
            })
            .catch(err => {
                dispatch(actionGoalFail(mamh, err.message))
            })
    }
}
