import { LoadingCellOverlay } from "../../StatelessComponents";

const { TableRow, TableCell, Input, Drawer } = require("@material-ui/core");

const CreateForm = ({ id, setId, desc, setDesc, outcome, setOutcome, loading }) => {
    return <Drawer>
        <TableRow className="font-italic">
            <TableCell align="center" className="font-weight-bold">
                <Input
                    className="bg-light rounded" fullWidth
                    inputProps={{ style: { textTransform: "uppercase" } }}
                    value={id}
                    onChange={e => setId(e.target.value)}
                />
            </TableCell>
            <TableCell>
                <Input
                    className="bg-light rounded p-2"
                    rowsMax={6} fullWidth multiline
                    value={desc}
                    onChange={e => setDesc(e.target.value)}
                />
            </TableCell>
            <TableCell>
                <Input
                    className="bg-light rounded" fullWidth
                    value={outcome}
                    onChange={e => setOutcome(e.target.value)}
                />
            </TableCell>
            {
                loading && <LoadingCellOverlay />
            }
        </TableRow>
    </Drawer>
}

export default CreateForm
