import { Card, CardActions, CardContent, CardHeader, CircularProgress, Dialog, IconButton, Input, TableCell, TableRow } from "@material-ui/core"
import { useState } from "react"
import { AiOutlineEdit } from "react-icons/ai"
import { BsTrash } from "react-icons/bs"
import { useDispatch } from "react-redux"
import { deleteCourseGoal } from "../../../services"
import { deleteGoalSuccess } from "../../../store/actions/courseGoal.action"

const Row = ({ row, mamh }) => {
    const [loading, setLoading] = useState(false)
    const [flag, setFlag] = useState('')
    const [goal, setGoal] = useState('')
    const [desc, setDesc] = useState('')
    const [outcome, setOutcome] = useState('')

    const dispatch = useDispatch()

    const handleSubmitDelete = async () => {
        setLoading(true)
        setFlag('')
        deleteCourseGoal(mamh, row.muctieu)
            .then(() => {
                console.log("DELETE_ROW")
                dispatch(deleteGoalSuccess(mamh, row.muctieu))
            })
            .catch(err => {
                setLoading(false)
                console.log(err)
            })
    }

    const handleToggleDelete = () => {
        setFlag('ConfirmDelete')
    }

    const handleToggleEdit = () => {
        setFlag(`${flag === 'EDIT' ? '' : 'EDIT'}`)
        setGoal(row.muctieu)
        setDesc(row.mota)
        setOutcome(row.cdr_ctdt)
    }

    return (
        <>
            <Dialog open={(flag === 'ConfirmDelete')} onClose={() => setFlag('')}>
                <Card className="p-3">
                    <CardHeader
                        disableTypography
                        title={`Xóa mục tiêu`}
                        className="page-title primary-logo-color"
                    />
                    <CardContent>
                        Xác nhận xóa môn&nbsp;
                    <span className="font-weight-bold font-italic">
                            {row.tenmh}
                        </span>.
                    </CardContent>
                    <CardActions>
                        <button
                            onClick={handleSubmitDelete}
                            className="btn btn-block btn-danger"
                        >
                            Xóa
                    </button>
                        <button
                            onClick={() => setFlag('')}
                            className="btn btn-block btn-light"
                        >
                            Hủy
                    </button>
                    </CardActions>
                </Card>
            </Dialog>
            <TableRow style={{ transform: "scale(1)" }}>
                <TableCell align="center">
                    {flag === 'EDIT'
                        ? <Input
                            value={goal}
                            onChange={e => setGoal(e.target.value)}
                            fullWidth
                        />
                        : row.muctieu
                    }
                </TableCell>
                <TableCell>
                    {flag === 'EDIT'
                        ? <Input
                            value={desc}
                            onChange={e => setDesc(e.target.value)}
                            fullWidth
                        />
                        : row.mota
                    }
                </TableCell>
                <TableCell align="center">
                    {flag === 'EDIT'
                        ? <Input
                            value={outcome}
                            onChange={e => setOutcome(e.target.value)}
                            fullWidth
                        />
                        : row.cdr_ctdt
                    }
                </TableCell>
                <TableCell align='center' className="px-0">
                    <IconButton
                        className="p-0 mx-3 text-primary"
                        onClick={handleToggleEdit}
                    >
                        <AiOutlineEdit size="24px" />
                    </IconButton>
                    <IconButton
                        className="p-0 mx-3 text-danger"
                        onClick={handleToggleDelete}
                    >
                        <BsTrash size="24px" />
                    </IconButton>
                </TableCell>
                {loading &&
                    <TableCell style={{ top: 0, left: 0 }}
                        className="position-absolute overlay flex-center p-0 w-100 h-100">
                        <CircularProgress className="text-white" />
                    </TableCell>
                }
            </TableRow>
        </>
    )
}

export default Row
