import { Breadcrumbs, makeStyles, Tab, Tabs } from "@material-ui/core"
import { useState } from "react"
import useBreadcrumbs, { routeConfig } from "../../../hooks/useBreadcrumbs"
import { MdKeyboardArrowRight } from 'react-icons/md'
import { Link } from "react-router-dom"

const useStyles = makeStyles({
    tabRoot: {
        minWidth: "unset",
        minHeight: "unset",
        fontSize: "15px",
        fontWeight: 700,
        padding: "0 5px",
        marginRight: "25px",
        textTransform: "none"

    },
    tabsRoot: {
        minHeight: "unset",
        marginTop: "1.5rem",
    },
    scroller: {
        paddingBottom: ".5rem"
    },
    flexContainer: {
        '&::after': {
            content: "''",
            display: "block",
            position: "absolute",
            height: "1px",
            width: "100%",
            backgroundColor: "rgba(0,0,0,.15)",
            bottom: 0
        }
    }

})

const CoursePage = () => {
    const [value, setValue] = useState(0)
    const classes = useStyles()

    const breadcrumbs = useBreadcrumbs(routeConfig)
    return <>
        <div>
            <Breadcrumbs separator={<MdKeyboardArrowRight size="1.2em" />} className="page-breadcrumb">
                {breadcrumbs.map(breadcrumb => (
                    <Link to={breadcrumb.path}>
                        {breadcrumb.name}
                    </Link>
                ))}
            </Breadcrumbs>
            <h3 className="page-title">
                Nhập môn ngành CNTT
            </h3>
            <Tabs
                value={value}
                indicatorColor="primary"
                textColor="primary"
                onChange={(e, newValue) => setValue(newValue)}
                classes={{
                    root: classes.tabsRoot,
                    flexContainer: classes.flexContainer,
                    scroller: classes.scroller
                }}
                style={{ overflowX: "auto" }}
                variant="scrollable"
            >
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Thông tin chung" />
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Mục tiêu" />
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Chuẩn đầu ra" />
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Đánh giá" />
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Nội dung" />
            </Tabs>
        </div>
    </>
}

export default CoursePage
