import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { useEffect, useState } from 'react';
import Axios from 'axios';
import { Breadcrumbs, IconButton } from '@material-ui/core';
import { Link } from 'react-router-dom';
import useBreadcrumbs, { routeConfig } from "../../../hooks/useBreadcrumbs"
import { MdKeyboardArrowRight } from 'react-icons/md';
import { AiFillInfoCircle } from 'react-icons/ai';
import { BsTrash } from 'react-icons/bs';

const CourseList = () => {
    const [rows, setRows] = useState([])
    const breadcrumbs = useBreadcrumbs(routeConfig)
    useEffect(() => {
        Axios.get('http://localhost:3001/api/monhoc')
            .then(data => setRows(data.data))
            .catch(err => alert(err))
    }, [])


    return (
        <>
            <div>
                <Breadcrumbs separator={<MdKeyboardArrowRight size="1.2em" />} className="page-breadcrumb">
                    {breadcrumbs.map(breadcrumb => (
                        <Link to={breadcrumb.path}>
                            {breadcrumb.name}
                        </Link>
                    ))}
                </Breadcrumbs>
                <h3 className="page-title">
                    Danh sách các môn học
            </h3>
                <hr />
            </div>
            <TableContainer component={Paper}>
                <Table style={{ minWidth: "650px" }}>
                    <TableHead>
                        <TableRow>
                            <TableCell>Mã môn học</TableCell>
                            <TableCell>Tên môn học</TableCell>
                            <TableCell>Số tín chỉ</TableCell>
                            <TableCell>Bộ môn</TableCell>
                            <TableCell>Phân loại</TableCell>
                            <TableCell align='center'>Xem chi tiết / Xóa</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows.map(row => (
                            <TableRow key={rows.name}>
                                <TableCell component="th" scope="row">
                                    {row.mamh}
                                </TableCell>
                                <TableCell>{row.tenmh}</TableCell>
                                <TableCell>{row.sotinchi}</TableCell>
                                <TableCell>{row.bomon}</TableCell>
                                <TableCell>{row.phanloai}</TableCell>
                                <TableCell align='center'>
                                    <Link color="primary" className="p-0 mx-3">
                                        <AiFillInfoCircle />
                                    </Link>
                                    <IconButton className="p-0 mx-3 text-danger">
                                        <BsTrash />
                                    </IconButton>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    )
}

export default CourseList
