import { useLocation } from 'react-router-dom'

const useBreadcrumbs = (routeConfig) => {
    const paths = []
    const route = useLocation().pathname
    var position = 0;
    while (position !== -1) {
        paths.push(route.slice(position))
        position = route.indexOf('/', position)
    }
    console.log(paths)
}

export default useBreadcrumbs
