import { Breadcrumbs, Button, makeStyles, Tab, Tabs } from "@material-ui/core"
import { useState } from "react"
import useBreadcrumbs, { routeConfig } from "../../../hooks/useBreadcrumbs"
import { MdKeyboardArrowLeft, MdKeyboardArrowRight } from 'react-icons/md'
import { Link, useHistory } from "react-router-dom"
import { useDispatch } from "react-redux"
import { fetchCourse } from "../../../store/actions/courseDetail.action"

const useStyles = makeStyles({
    tabRoot: {
        minWidth: "unset",
        minHeight: "unset",
        fontSize: "15px",
        fontWeight: 700,
        padding: "0 5px",
        marginRight: "25px",
        textTransform: "none"

    },
    tabsRoot: {
        minHeight: "unset",
        marginTop: "1.5rem",
    },
    flexContainer: {
        minHeight: "40px",
        '&::after': {
            content: "''",
            display: "block",
            position: "absolute",
            height: "1px",
            width: "100%",
            backgroundColor: "rgba(0,0,0,.15)",
            bottom: 0
        }
    }

})

const CoursePage = () => {
    const [value, setValue] = useState(0)
    const classes = useStyles()
    const history = useHistory()
    const dispatch = useDispatch()
    dispatch(fetchCourse("ASDDFASD"))


    const breadcrumbs = useBreadcrumbs(routeConfig)
    return <>
        <div>
            <Breadcrumbs separator={<MdKeyboardArrowRight size="1.2em" />} className="page-breadcrumb">
                {breadcrumbs.map(breadcrumb => (
                    <Link to={breadcrumb.path}>
                        {breadcrumb.name}
                    </Link>
                ))}
            </Breadcrumbs>
            <div className="d-flex justify-content-between align-items-center flex-wrap">
                <h3 className="page-title">
                    Thêm môn học mới
                </h3>
                <Button
                    size="small"
                    className="text-transform-none p-0 mr-2 "
                    onClick={() => history.push('/course')}
                >
                    <MdKeyboardArrowLeft size="1.2rem" />
                        Xem danh sách các môn học
                    </Button>
            </div>
            <Tabs
                value={value}
                indicatorColor="primary"
                textColor="primary"
                onChange={(e, newValue) => setValue(newValue)}
                classes={{
                    root: classes.tabsRoot,
                    flexContainer: classes.flexContainer,
                    scroller: classes.scroller
                }}
                style={{ overflowX: "auto" }}
                variant="scrollable"
            >
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Thông tin chung" />
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Mục tiêu" />
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Chuẩn đầu ra" />
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Đánh giá" />
                <Tab
                    classes={{ root: classes.tabRoot }}
                    label="Nội dung" />
            </Tabs>
        </div>
    </>
}

export default CoursePage
