import { Breadcrumbs, Button, Card, CardActions, CardContent, CardHeader, IconButton, Snackbar } from "@material-ui/core"
import { useState } from "react"
import axios from 'axios'
import useBreadcrumbs, { routeConfig } from "../../../hooks/useBreadcrumbs"
import { MdKeyboardArrowRight } from 'react-icons/md'
import { Link } from "react-router-dom"
import styled from "styled-components"

const InputLabel = styled.label`
    font-size: 14px;
`

const Input = styled.input`
 &:focus{
     outline:none;
     box-shadow:none;
 }
`

const NewCourse = () => {
    const [mamonhoc, setMamonhoc] = useState('')
    const [tenmonhoc, setTenmonhoc] = useState('')
    const [tinchi, setTinchi] = useState(null)
    const [phanloai, setPhanloai] = useState('')
    const [mota, setMota] = useState('')
    const [bomon, setBomon] = useState('')
    const [alert, setAlert] = useState(false)
    const breadcrumbs = useBreadcrumbs(routeConfig)

    const handleSubmit = e => {
        axios.post('http://localhost:3001/api/monhoc', {
            mamh: mamonhoc,
            tenmh: tenmonhoc,
            sotinchi: tinchi,
            phanloai,
            mota,
            mabomon: bomon
        }).then(res => {
            setAlert(true)
        }).catch(err => {
            setAlert(true)
        })
    }

    return <>
        <div>
            <Breadcrumbs separator={<MdKeyboardArrowRight size="1.2em" />} className="page-breadcrumb">
                {breadcrumbs.map(breadcrumb => (
                    <Link to={breadcrumb.path}>
                        {breadcrumb.name}
                    </Link>
                ))}
            </Breadcrumbs>
            <h3 className="page-title">
                Thêm môn học mới
            </h3>
            <hr />
        </div>
        <Card className="feature-card">
            <CardHeader
                title="Nhập thông tin môn học"
                className="border-bottom "
                disableTypography
            />
            <CardContent className="border-bottom">
                <form>
                    <div className="row">
                        <div className="form-group col-2">
                            <InputLabel>Mã môn học</InputLabel>
                            <Input type="text" className="form-control rounded-pill" />
                        </div>
                        <div className="form-group col">
                            <InputLabel>Tên môn học</InputLabel>
                            <input type="text" className="form-control" />
                        </div>
                        <div className="form-group col-1">
                            <InputLabel>TC</InputLabel>
                            <input type="number" className="form-control" />
                        </div>
                    </div>
                </form>
            </CardContent>
            <CardActions className="justify-content-end">
                <Button
                    onClick={handleSubmit}
                    variant="contained"
                    color="primary"
                >
                    Tạo môn học mới
                    </Button>
            </CardActions>
        </Card>
        <Snackbar
            anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left'
            }}
            open={alert}
            autoHideDuration={1500}
            message="Success"
            action={
                <>
                    <IconButton
                        size="small"
                        aria-label="close"
                        color="inherit"
                        onClick={() => setAlert(false)}
                    >
                        X
                    </IconButton>
                </>
            }
        />
    </>
}

export default NewCourse
