import { useLocation } from 'react-router-dom'

const useBreadcrumbs = (routeConfig) => {
    const paths = []
    const route = useLocation().pathname
    var position = 1;
    while (position !== -1) {
        paths.push(route.slice(0, position))
        position = route.indexOf('/', position + 1)
        if (position === -1 && route !== "/")
            paths.push(route)
    }
    console.log(paths)
}

export default useBreadcrumbs
