import { Button, Divider, Paper } from "@material-ui/core"
import { useState } from "react"
import { AiOutlineEdit } from "react-icons/ai"
import styled from "styled-components"

const Header = styled.h2`
    font-weight:700;
    font-size:17px;
    margin-bottom:0;
`

const Label = styled.p`
    font-weight:700;
    font-size:13px;
    margin-bottom:0;
`

const Content = styled.p`
    white-space:pre;
    font-size:14px;
    margin-bottom:0;
`
const ContentRow = ({label,content}) => (
    <div className="row mb-3">
        <Label className="col-2 section-title-color">
            {label}:
        </Label>
        <Content className="col">
            {content}
        </Content>
    </div>
)

const CourseInfo = ({ course }) => {
    const [edit, setEdit] = useState(false)

    return (
        <Paper>
            <div className="px-3 py-2 d-flex align-items-center justify-content-between">
                <Header>
                    Tổng quan môn học
                </Header>
                <Button
                    className="text-transform-none"
                    onClick={() => setEdit(!edit)}
                >
                    Chỉnh sửa&nbsp;<AiOutlineEdit size="17px" />
                </Button>
            </div>
            <Divider />
            <div className="px-4 pt-3 pb-2">
                <ContentRow 
                    label="Tên môn học"
                    content={course.tenmh}
                />
                <ContentRow 
                    label="Mã môn học"
                    content={course.mamh}
                />
                <ContentRow 
                    label="Số tín chỉ"
                    content={course.sotinchi}
                />
                <ContentRow 
                    label="Bộ môn"
                    content={course.bomon}
                />
                <ContentRow 
                    label="Phân loại"
                    content={course.phanloai}
                />
                <ContentRow 
                    label="Mô tả"
                    content={course.mota}
                />
            </div>
        </Paper>
    )
}

export default CourseInfo
