import { Backdrop, Breadcrumbs, Button, Card, CardActions, CardContent, CardHeader, CircularProgress, Dialog } from "@material-ui/core"
import { useState } from "react"
import useBreadcrumbs, { routeConfig } from "../../../hooks/useBreadcrumbs"
import { MdKeyboardArrowRight } from 'react-icons/md'
import { Link } from "react-router-dom"
import { createCourse } from "../../../services"
import { NewCourseForm } from "./form"

const NewCourse = () => {
    const [mamh, setMamh] = useState('')
    const [tenmh, setTenmh] = useState('')
    const [sotinchi, setsotinchi] = useState('')
    const [phanloai, setPhanloai] = useState('')
    const [mota, setMota] = useState('')
    const [bomon, setBomon] = useState('')
    const [posting, setPosting] = useState({
        isPosting: false,
        response: {
            status: "",
            message: ""
        }
    })
    const [errors, setErrors] = useState({
        mamh: '', tenmh: '', sotinchi: '', phanloai: '', bomon: ''
    })
    const breadcrumbs = useBreadcrumbs(routeConfig)

    const validateForm = () => {
        const error = {}
        if (mamh === '')
            error.mamh = 'Nhập mã môn học'
        if (tenmh === '')
            error.tenmh = 'Nhập tên môn học'
        if (phanloai === '')
            error.phanloai = 'Chọn phân loại môn'
        if (bomon === '')
            error.bomon = 'Chọn bộ môn'
        if (sotinchi === '')
            error.sotinchi = 'Nhập số tín chỉ'
        setErrors({ ...error })
        if (Object.keys(error).length === 0)
            return true
        else
            return false
    }

    const handleDialogClose = () => {
        setPosting({
            isPosting: false,
            response: {
                status: "",
                message: ""
            }
        })
    }

    const handleSubmit = async (e) => {
        if (!validateForm())
            return
        setPosting({ ...posting, isPosting: true })
        const data = {
            mamh, tenmh, sotinchi, phanloai, mota, bomon
        }
        console.log(data)
        try {
            await createCourse(data)
            setPosting({
                isPosting: false,
                response: {
                    status: "success",
                    message: "Tạo môn học thành công!"
                }
            })
            return
        }
        catch (err) {
            setPosting({
                isPosting: false,
                response: {
                    status: "error",
                    message: err ? "Lỗi máy chủ" : err.response.message
                }
            })
        }
    }

    return <>
        <Backdrop open={posting.isPosting} style={{ zIndex: 2000 }}>
            <CircularProgress className="text-white" />
        </Backdrop>
        <Dialog
            open={posting.response.status}

        >

        </Dialog>
        <div>
            <Breadcrumbs separator={<MdKeyboardArrowRight size="1.2em" />} className="page-breadcrumb">
                {breadcrumbs.map(breadcrumb => (
                    <Link to={breadcrumb.path}>
                        {breadcrumb.name}
                    </Link>
                ))}
            </Breadcrumbs>
            <h3 className="page-title">
                Thêm môn học mới
            </h3>
            <hr />
        </div>
        <Card className="feature-card">
            <CardHeader
                title="Nhập thông tin môn học"
                className="border-bottom "
                disableTypography
            />
            <CardContent className="border-bottom overflow-auto">
                <NewCourseForm
                    props={{
                        mamh, setMamh, tenmh, setTenmh, sotinchi, setsotinchi,
                        phanloai, setPhanloai, mota, setMota, bomon, setBomon,
                        errors, setErrors
                    }}
                />
            </CardContent>
            <CardActions className="justify-content-end">
                <Button
                    onClick={handleSubmit}
                    variant="contained"
                    color="primary"
                    type="submit"
                    className="text-transform-none font-weight-bold"
                >
                    Tạo môn học mới
                    </Button>
            </CardActions>
        </Card>
    </>
}

export default NewCourse
