import { Button, Divider, Input, Paper } from "@material-ui/core"
import { useState } from "react"
import { AiOutlineEdit } from "react-icons/ai"
import styled from "styled-components"

const Header = styled.h2`
    font-weight:700;
    font-size:17px;
    margin-bottom:0;
`

const Label = styled.label`
    font-weight:700;
    font-size:13px;
    margin-bottom:0;
    width:120px;
    padding:0 15px;
`

const Content = styled.p`
    white-space:pre-line;
    word-wrap:break-word;
    font-size:15px;
    margin-bottom:0;
`
const ContentContainer = styled.div`
    min-width:500px;
`

const BoMonSelect = (props) => (
    <select
        className="bg-light form-control"
        value={props.value}
        onChange={e => props.setValue(e.target.value)}
    >
        <option selected value="" disabled>-- Chọn bộ môn --</option>
        <option>Tin học cơ sở</option>
        <option>Công nghệ phần mềm</option>
        <option>Hệ thống thông tin</option>
        <option>Mạng và an ninh mạng</option>
    </select>
)

const PhanLoaiSelect = (props) => (
    <select
        className="bg-light form-control"
        value={props.value}
        onChange={e => props.setValue(e.target.value)}
    >
        <option selected value="" disabled>-- Chọn phân loại --</option>
        <option>Tùy chọn</option>
        <option>Bắt buộc</option>
    </select>
)

const ContentRow = ({ label, content, edit, handle, select, multiline, number }) => (
    <div className="row mb-3 d-flex align-items-center">
        <Label className="section-title-color">
            {label}:
        </Label>
        <Content className="col">
            {!edit
                ? content
                : select
                    ? label === "Bộ môn"
                        ? <BoMonSelect value={content} setValue={handle} />
                        : <PhanLoaiSelect value={content} setValue={handle} />
                    : <Input
                        value={content}
                        onChange={e => handle(e.target.value)}
                        multiline={multiline}
                        fullWidth
                        className="bg-light p-2"
                        type={`${number ? "number" : "text"}`}
                    />
            }
        </Content>
    </div>
)

const CourseInfo = ({ course }) => {
    const [edit, setEdit] = useState(false)
    const [tenmh, setTenmh] = useState(course.tenmh)
    const [sotinchi, setSotinchi] = useState(course.sotinchi)
    const [bomon, setBomon] = useState(course.bomon)
    const [phanloai, setPhanloai] = useState(course.phanloai)
    const [mota, setMota] = useState(course.mota)

    const handleCloseEdit = () => {
        setTenmh(course.tenmh)
        setSotinchi(course.sotinchi)
        setBomon(course.bomon)
        setPhanloai(course.phanloai)
        setMota(course.mota)
        setEdit(false)
    }

    const handleSubmitEdit = () => {
        setEdit(false)
    }

    return (
        <Paper>
            <div className="px-3 py-2 d-flex flex-wrap align-items-center justify-content-between">
                <Header>
                    Tổng quan môn học
                </Header>
                {edit
                    ? (
                        <div>
                            <Button
                                className="text-transform-none"
                                onClick={handleSubmitEdit}
                                variant="contained"
                                color="primary"
                            >
                                Sửa
                            </Button>
                            <Button
                                className="text-transform-none ml-2"
                                onClick={handleCloseEdit}
                                variant="contained"
                            >
                                Đóng
                            </Button>
                        </div>
                    )
                    : (
                        <Button
                            className="text-transform-none"
                            onClick={() => setEdit(true)}
                        >
                            Chỉnh sửa&nbsp;<AiOutlineEdit size="17px" />
                        </Button>
                    )
                }
            </div>
            <Divider />
            <div style={{ overflowX: "auto" }}>
                <ContentContainer className="px-5 pt-3 pb-2">
                    <ContentRow
                        label="Mã môn học"
                        content={course.mamh}
                    />
                    <ContentRow
                        label="Tên môn học"
                        content={tenmh}
                        edit={edit}
                        handle={setTenmh}
                    />
                    <ContentRow
                        label="Số tín chỉ"
                        content={sotinchi}
                        edit={edit}
                        handle={setSotinchi}
                        number
                    />
                    <ContentRow
                        label="Bộ môn"
                        content={bomon}
                        edit={edit}
                        handle={setBomon}
                        select
                    />
                    <ContentRow
                        label="Phân loại"
                        content={phanloai}
                        edit={edit}
                        handle={setPhanloai}
                        select
                    />
                    <ContentRow
                        label="Mô tả"
                        content={mota}
                        edit={edit}
                        handle={setMota}
                        multiline
                    />
                </ContentContainer>
            </div>
        </Paper>
    )
}

export default CourseInfo
